mapper.update:
UPDATE Getway SET
GetwayMark = #{getway.getwayMark}, SpareNode = #{getway.spareNode}, NodeNum = #{getway.nodeNum}, Status = #{getway.status}, NowTemper = #{getway.nowTemper}, Humidity = #{getway.nowHumidity}, TimeInter = #{getway.timeInter}, memo = #{getway.memo},
WHERE GetwayId = #{getway.getwayId};

UPDATE Getway
SET NowTemper = #{temper}, Humidity = #{humidity}
WHERE GetwayId = #{getwayId};

UPDATE Getway SET
SpareNode = #{getway.spareNode}, NodeNum = #{getway.nodeNum}, Status = #{getway.status}, NowTemper = #{getway.nowTemper}, Humidity = #{getway.nowHumidity}, TimeInter = #{getway.timeInter}, memo = #{getway.memo},
WHERE GetwayMark = #{getway.getwayMark};

choose.when:
SELECT g.* FROM Getway g, Unit u, Rent r WHERE r.GetwayId = g.GetwayId AND u.UnitId = r.UnitId
AND GETDATE() BETWEEN r.StartTime AND r.EndTime AND r.UnitId = #{unitId} AND g.GetwayId =
#{getwayId}

SELECT g.* FROM Getway g, Unit u, Rent r WHERE r.GetwayId = g.GetwayId AND u.UnitId = r.UnitId
AND GETDATE() BETWEEN r.StartTime AND r.EndTime AND r.UnitId = #{unitId} AND GetwayMark =
#{mark}

mapper.select:
SELECT COUNT(1)
FROM Getway
WHERE GetwayMark = #{mark}
